import React from 'react'
import { ToggleButton, ToggleButtonGroup, Stack } from 'react-bootstrap';

export default function FederationForm({ federations, setFederationFilter, federationFilter }){
  const handleChange = (val) => setFederationFilter(val);
  return (
    <Stack>
              <Stack gap={2}>
          {federationOptions.map((federation) => {
            return <div className="p-2">{federation} </div>
          })}
        </Stack>
      <ToggleButtonGroup type="checkbox" value={federationFilter} onChange={handleChange}>
        {federations.map((federation) => {
          return (
            <ToggleButton key={federation} id={`tbg-btn${federation}`} value={federation}>
              {federation}
            </ToggleButton>
            )
          })
        }
      </ToggleButtonGroup>
    </Stack>

  )
}
