import React, {useEffect, useState} from 'react'
import { Table, InputGroup, Form, Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function App(){
  const [search, setSearch] = useState('')
  console.log(search)
  const [userData, setUserData] = useState([])

  useEffect(() => {
    fetch("/api")
      .then(response => {
        return response.json()
      })
      .then(data => {
        setUserData(data)
      })
  }, [])

  function filterNames(subset){
    return userData.filter(meet => meet.name.toLowerCase().includes(subset.toLowerCase()))
  }

  return (
    <Container>
      <h1 className='text-center mt-4'>Powerlifting Meets</h1>
      <Form>
        <InputGroup className='my-3'>
          <Form.Control onChange={(e) => setSearch(e.target.value)} placeholder='filter events'></Form.Control>
        </InputGroup>
      </Form>
      <Table>
        <thead>
          <tr>
            <th>Federation</th>
            <th></th>
            <th>When</th>
            <th>Where</th>
          </tr>
        </thead>
        <tbody>
        {filterNames(search).map((item) => (
          <tr>
            <td>{item.federation}</td>
            <td>{item.name}</td>
            <td>{item.when}</td>
            <td>{item.where}</td>
          </tr>
        ))}
        </tbody>
      </Table>
    </Container>
  )
}


export default App
