import React, {useEffect, useState} from 'react'
import { Table, InputGroup, Form, Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

import { useQuery, useMutation } from '@tanstack/react-query';

function App(){
  // HOW TO REPLACE USE STATE WITH REACT QUERY
  const [search, setSearch] = useState('')
  const [meetData, setMeetData] = useState([])
  const [page, setPage] = useState(0)

  async function fetchMeetData(){
    fetch("/meets")
      .then((response) => {
        return response.json()
      })
      .then(data => setMeetData(data))
  }


  if (postsQuery.isLoading) {return <h1>Loading...</h1>}
  return (
    <Container>
      <h1 className='text-center mt-4'>Powerlifting Meets</h1>
      <Form>
        <InputGroup className='my-3'>
          <Form.Control onChange={(e) => setSearch(e.target.value)} placeholder='filter events'></Form.Control>
        </InputGroup>
      </Form>
      <Table>
        <thead>
           <tr>
            <th>Federation</th>
            <th>Name</th>
            <th>When</th>
            <th>State</th>
            <th>Where</th>
          </tr>
        </thead>
        <tbody>
        {postsQuery.data.map((item) => (
          <tr>
            <td>{item.federation}</td>
            <td>{item.name}</td>
            <td>{item.date}</td>
            <td>{item.state}</td>
            <td>{item.address}</td>
          </tr>
        ))}
        </tbody>
      </Table>
      <button disabled={page === 0} onClick={() => setPage(page - 1)}>Last Page</button>
      <button onClick={() => setPage(page + 1)}>Next Page</button>
      <button onClick={() => setPage(0)}>Reset</button>
    </Container>
  )
}

export default App
