    const mapHandler = (event) => {
        console.log(event.target.dataset.name)

        let newState = event.target.dataset.name
        stateConfig[newState] = { fill: "navy" }

        }

    const config = () => {
        console.log(stateConfig)
        return stateConfig
    }
    return (
        <USAMap customize={config()} onClick={mapHandler} />
    )
  }
