import React, {useEffect, useState} from 'react'
import { Table, InputGroup, Form, Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { QueryClient, QueryClientProvider} from "@tanstack/react-query"
import { useQuery, userMutation } from '@tanstack/react-query';
const queryClient = new QueryClient()
function App(){
  const [search, setSearch] = useState('')
  console.log(search)
  const [meetData, setMeetData] = useState([])
  const [page, setPage] = useState(0)

  useEffect(() => {
    fetch("/meets")
      .then(response => {
        return response.json()
      })
      .then(data => {
        setMeetData(data)
      })
  }, [])

  return (
    <Container>
      <QueryClientProvider client={queryClient}>
        <h1 className='text-center mt-4'>Powerlifting Meets</h1>
        <Form>
          <InputGroup className='my-3'>
            <Form.Control onChange={(e) => setSearch(e.target.value)} placeholder='filter events'></Form.Control>
          </InputGroup>
        </Form>
        <Table>
          <thead>
            <tr>
              <th>Federation</th>
              <th>Name</th>
              <th>When</th>
              <th>State</th>
              <th>Where</th>
            </tr>
          </thead>
          <tbody>
          {meetData.map((item) => (
            <tr>
              <td>{item.federation}</td>
              <td>{item.name}</td>
              <td>{item.date}</td>
              <td>{item.state}</td>
              <td>{item.address}</td>
            </tr>
          ))}
          <button disabled={page === 0} onClick={() => setPage(page - 1)}>Last Page</button>
          <button onClick={() => setPage(page + 1)}>Next Page</button>
          <button onClick={() => setPage(0)}>Reset</button>
          </tbody>
        </Table>
      </QueryClientProvider>
    </Container>
  )
}

export default App
