import React, {useEffect, useState} from 'react'
import { Table, InputGroup, Form, Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useQuery, useMutation } from '@tanstack/react-query';

function App(){
  // HOW TO REPLACE USE STATE WITH REACT QUERY
  const [search, setSearch] = useState('')
  const [meetData, setMeetData] = useState([])
  const [data, setData] = useState({})
  const [page, setPage] = useState(0)
  const [pageCount, setPageCount] = useState(0)


  useEffect(() => {
    fetch(`/meets?page=${page}`)
      .then((response) => {
        return response.json()
      })
      .then(data => {
        setMeetData(data.info)
        setPageCount(data.pageCount
        )}
      )
  }, [page])
  return (
    <Container>
      <h1 className='text-center mt-4'>Powerlifting Meets</h1>
      <Form>
        <InputGroup className='my-3'>
          <Form.Control onChange={(e) => setSearch(e.target.value)} placeholder='filter events'></Form.Control>
        </InputGroup>
      </Form>
      <Table>
        <thead>
           <tr>
            <th>Federation</th>
            <th>Name</th>
            <th>When</th>
            <th>State</th>
            <th>Where</th>
          </tr>
        </thead>
        <tbody>
        {meetData.map((item) => (
          <tr>
            <td>{item.federation}</td>
            <td>{item.name}</td>
            <td>{item.date}</td>
            <td>{item.state}</td>
            <td>{item.address}</td>
          </tr>
        ))}
        </tbody>
      </Table>
      <button disabled={page === 0} onClick={() => setPage(page - 1)}>Last Page</button>
      <button disabled={page === pageCount - 1} onClick={() => setPage(page + 1)}>Next Page</button>
      <button onClick={() => setPage(0)}>Reset</button>
      <select> {Array(pageCount).fill(null)} </select>
    </Container>
  )
}

export default App
