import React from 'react'
import { ToggleButton, ToggleButtonGroup, Button } from 'react-bootstrap';
import AMPImage from '../images/amp.png'
import USAPLImage from '../images/usapl.png'
import USPAImage from '../images/uspa.png'

const fedImages = {"USPA": USPAImage, "USAPL": USAPLImage, "AMP": AMPImage}

export default function FederationForm({ federations, setFederationFilter, federationFilter }){
  const handleChange = (val) => setFederationFilter(val);
  return (
    <div>
      <h2 >Federations</h2>
      <ToggleButtonGroup vertical aria-label="medium secondary button group" type="checkbox" value={federationFilter} onChange={handleChange}>
            {federations.map((federation) => {
              return (
                <ToggleButton style={{width: "100%"}} key={federation} id={federation} value={federation}>
                  <img style={{width:"auto", height: "25%"}} src={fedImages[federation]} alt={federation} />
                </ToggleButton>
                )
              })
            }
      </ToggleButtonGroup>
      <Button as="input" type="reset" value="Reset" onClick={() => setFederationFilter(federations)} />
    </div>
  
  )
}
